import React, { useState, useEffect } from 'react';
import { useMetaTags } from '../hooks/useMetaTags';

export const ExampleComponent: React.FC = () => {
  const [metaTags, setMetaTags] = useState<any>(null);
  const { componentRef, isLoading, error } = useMetaTags({
    useAI: Boolean(process.env.REACT_APP_OPENAI_API_KEY),
  });

  useEffect(() => {
    const tags = document.head.querySelectorAll('meta');
    const currentTags = Array.from(tags).map(tag => ({
      name: tag.getAttribute('name'),
      content: tag.getAttribute('content')
    }));
    setMetaTags(currentTags);
  }, [isLoading]);

  return (
    <div>
      <div ref={componentRef}>
        <h1>Example Page</h1>
        <p>This is a comprehensive guide about artificial intelligence and machine learning.
           We cover topics like neural networks, deep learning, and natural language processing.</p>
      </div>

      {isLoading && <div>Generating meta tags...</div>}
      {error && <div>Error: {error}</div>}
      
      {metaTags && (
        <div style={{ marginTop: '20px', padding: '10px', background: '#f5f5f5' }}>
          <h3>Generated Meta Tags:</h3>
          <pre>{JSON.stringify(metaTags, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};
