import { useEffect, useRef } from 'react';
import { MetaConfig, MetaTagGenerator } from '../utils/MetaTagGenerator';

export const useMetaTags = (config: MetaConfig) => {
  const componentRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    let tags: HTMLMetaElement[] = [];

    const generateTags = async () => {
      if (config.useAI) {
        config.elementRef = componentRef;
      }
      tags = await MetaTagGenerator.generate(config);
    };

    generateTags();

    return () => {
      // Cleanup tags when component unmounts
      tags.forEach(tag => tag.remove());
      if (config.canonical) {
        const canonical = document.querySelector('link[rel="canonical"]');
        canonical?.remove();
      }
    };
  }, [config]);

  return componentRef;
};
