import OpenAI from 'openai';

export class MetaAIService {
  private static openai = new OpenAI({
    apiKey: process.env.REACT_APP_OPENAI_API_KEY,
    dangerouslyAllowBrowser: true
  });

  static async generateMetaSuggestions(content: string) {
    try {
      const completion = await this.openai.chat.completions.create({
        messages: [{
          role: "system",
          content: "Generate SEO meta tags based on the following content. Return JSON with title, description, and keywords."
        }, {
          role: "user",
          content: content
        }],
        model: "gpt-3.5-turbo",
      });

      const result = JSON.parse(completion.choices[0].message.content || '{}');
      return result;
    } catch (error) {
      console.error('Error generating meta tags:', error);
      return {
        title: 'Default Title',
        description: 'Default Description',
        keywords: ['default', 'keywords']
      };
    }
  }

  static extractContentFromComponent(element: HTMLElement): string {
    return element.innerText || element.textContent || '';
  }
}
